import { Component } from '@angular/core';
import { Observable } from 'rxjs';
import { Task } from '../model/task';
import { ApiService } from '../services/api.service';
import { Guid } from "guid-typescript";

@Component({
  selector: 'app-search-tasks',
  templateUrl: './search-tasks.component.html',
  styleUrls: ['./search-tasks.component.css']
})
export class SearchTasksComponent {
  listTask$ = this.apiService.getListTasks();

  task$ = new Observable<Task>;

  constructor(private apiService: ApiService) {
  }

  getTaskById(id: Guid) {
    this.task$ = this.apiService.getTaskById(id);
  }

}

//export class Example {
//  public id: Guid;
//  constructor() {
//    this.id = Guid.create(); // ==> b77d409a-10cd-4a47-8e94-b0cd0ab50aa1
//  }
//}
<ng-container *ngIf="listTask$ | async as listTask">

  <p>tasks-list works!</p>

  <div *ngFor="let task of listTask">
    <div class="card text-dark mt-5 text-center">
      <div class="card-body" style="padding: 20px; background-color:#e5ecf26e">

        <h6 class="card-title">{{task.title}}</h6>
        <div class="card-title" style="color:#ff3939">
          {{task.description}}
        </div>

          <div class="d-grid text-center g-2">
            <div class="row">
              <div class="col">

                <div class="col m-3">
                  <i class="text-primary fas fa-flag-checkered" style="font-size:20px;"></i>
                </div>

                <div class="col">
                  <i class="fa-regular fa-clock"></i>
                  {{task.dueDate | date:'shortTime'}}
                </div>


              </div>

              <div class="col">
                <div style="text-align:center">
                  <br />
                  <i class="fas fa-spinner fa-spin"></i>
                </div>
              </div>

              <div class="col">
                <div class="col m-3">
                  <i class="text-primary fas fa-check" style="font-size:20px;"></i>
                </div>

                <div class="col">
                  <i class="fa-regular fa-clock"></i>
                  {{task.completed | date:'shortTime'}}
                </div>
              </div>

              <div class="row">
                <div class="col">

                </div>

                <div class="col m-3" style="text-align:center">
                  <a class="btn btn-secondary btn-lg mt-2">
                    <i class="fas fa-ticket"></i>
                    Details
                  </a>
                </div>

                <div class="col">

                </div>
              </div>
              
            </div>
          </div>

      </div>
    </div>
  </div>

</ng-container>
