import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router'
import { Guid } from 'guid-typescript';
import { Observable } from 'rxjs';
import { ApiService } from '../services/api.service';


@Component({
  selector: 'app-details-task',
  templateUrl: './details-task.component.html',
  styleUrls: ['./details-task.component.css']
})

export class DetailsTaskComponent implements OnInit {

  Id: string = 'not loaded';
  task$: any;

  constructor(private route: ActivatedRoute, private apiService: ApiService) {
  }

  ngOnInit(): void {
    this.route.paramMap.subscribe(p => this.findTask(p.get("Id")));

    //this.getTaskById(this.Id).subscribe(
    //  res => {
    //    this.task$ = res;
    //});
  }

  private findTask = (Id: string | null) => {
    this.Id = Id ?? 'not passed';

    this.apiService.getTaskById(this.Id).subscribe(task => this.task$ = task);
  }

  getTaskById(id: string) {
    return this.apiService.getTaskById(id)
  }

}
<p>details-task works!</p>

<h2>Task Id: {{Id}}</h2>

<div class="card text-left" >
  <div class="card-header">  </div>
  <div class="card-block">
    <h4 class="card-title">Title : {{task$.title}}</h4>
    <p class="card-text">
      Description : {{task$.description}}<br />
      DueDate: {{task$.dueDate}}<br />
      Completed: {{task$.completed}}
    </p>
  </div>
</div>
